import Ndata from "./NdData";
import Slider from "react-slick";
const Carts = () => {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1
        , responsive: [
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 3,
                slidesToScroll: 3,
                infinite: true,
                dots: true
              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                initialSlide: 2
              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
          ],
      };
    return (
        <>
        <Slider {...settings}>
            <div className="content grid ">
                {
                    Ndata.map((vale, index) =>{
                        return(
                            <div className="" key={index}>
                                <div className="">
                                    <img src={vale.cover} alt="" />
                                </div>
                                <h4>{vale.name}</h4>
                                <span>{vale.price}</span>
                            </div>
                        )
                    } )
                }
            </div>
            </Slider>
        </>
    );
}

export default Carts;
