import { useContext } from "react";
import CartContext from "../../context/CartContext";

const Cart = () => {
    const {items, addToCart, decreaseQty } = useContext(CartContext)
    return ( 
        <>
                <section className="cart-items">
                    <div className="container d_flex">
                        <div className="cart-details">
                            {items.length === 0 && <h1 className="no-items product">No Items are in cart</h1>}
                            {items.map((item) => {
                                const productQty = item.productItems.price * item.productItems.qty
                                return (
                                    <div className="cart-list product d_flex">
                                        <div className="img">
                                            <img src={item.productItems.cover}/>
                                        </div>
                                        <div>
                                            <h3>{item.productItems.name}</h3>
                                            <h4>
                                                {item.productItems.price}.00 * {item.productItems.qty}
                                                <span>${productQty}.00</span>
                                            </h4>
                                        </div>
                                        <div className="cart-items-function">
                                            <div className="removeCart">
                                                <button>
                                                    <i className="fa -solid fa-xmark"></i>
                                                </button>
                                            </div>
                                            <div className="cartControl d_flex">
                                                <button className="incCart" onClick={() => addToCart(item)}>
                                                    <i className=" fa fa-plus"></i>
                                                </button>
                                                <button className="incCart" onClick={() => decreaseQty(item)}>
                                                    <i className=" fa fa-minus"></i>
                                                </button>
                                            </div>
                                        </div>
                                        <div className="cart-item-price">

                                        </div>
                                    </div>

                                )
                            } )}
                            <div className="cart-total product">

                            </div>
                        </div>
                    </div>
                </section>
        </>
     );
}
 
export default Cart;
